            var first;
            menuOptions.forEach(function(opt) {
                var item = $('<li>').appendTo(list);
                var link = $('<a href="#"></a>').text(opt.label).appendTo(item);
                link.on("click", function(evt) {
                    evt.preventDefault();
                    if (opt.onselect) {
                        opt.onselect();
                    }
                    menu.hide();
                })
                if (!first) { first = link}
            })
